mapper.update:
    UPDATE seckill
    SET number = number - 1
    WHERE seckill_id = #{seckillId}
      AND start_time

          <=

#{killTime}
      AND end_time >= #{killTime}
      AND number > 0

mapper.select:
select *
from seckill AS s
where s.seckill_id = #{seckillId}

select *
from seckill AS s
order by create_time DESC LIMIT #{offset}, #{limit}
